mapper.insert:
INSERT INTO article
	SET regDate = NOW()
		, updateDate = NOW()
		, memberId = #{memberId}
		, boardId = #{boardId}
		, title = #{title}
		, `body` = #{body}

mapper.select:
SELECT A.*, M.nickname AS `writerName` FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id WHERE A.boardId = #{boardId}
AND A.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.body LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( A.title LIKE CONCAT('%', #{searchKeyword}, '%') OR A.body LIKE CONCAT('%', #{searchKeyword}, '%') )
ORDER BY A.id DESC LIMIT #{limitStart}, #{itemsInAPage}

mapper.update:
UPDATE article

		updateDate = NOW()

			, title = #{title}


			, `body` = #{body}


	WHERE id = #{id}
